class User:
    def __init__(self, name, email):
        self.name = name
        self.email = email
        self.is_active = True

    def deactivate(self):
        self.is_active = False

    def send_email(self, message):
        if self.is_active:
            print(f"Sending to {self.email}: {message}")
        else:
            print("User is inactive")

